// Tree 类
// @param words 该节点包含的单词
// @param children 子节点
class Tree {
  public words: string[] = [];
  public children: { [key: string]: Tree } = {};

  constructor(words: string[] = [], children: { [key: string]: Tree } = {}) {
    this.words = words;
    if (children) {
      for (const key in children) {
        if (Object.prototype.hasOwnProperty.call(children, key)) {
          const element = children[key];
          this.children = { ...this.children, [key]: element };
        }
      }
    }
  }
}

// 将 word 添加到字典树
const addWord = function (root: Tree, word: string) {
  let cur = root;
  for (let ch of word) {
    // 遍历 word 字符串，为每个字母创建子 Tree
    if (!cur.children[ch]) {
      cur.children[ch] = new Tree();
    }

    // 将当前 word 添加到子 Tree 的 words 中，如：{ f: { words: ['foo'] } }
    cur = cur.children[ch];
    cur.words.push(word);

    // 对子串按字典序进行排序，按题目要求保留前 3 位
    cur.words = cur.words.sort();
    if (cur.words.length > 3) {
      cur.words.pop();
    }
  }
};

function suggestedProducts(products: string[], searchWord: string): string[][] {
  // 创建一个根节点，遍历 products 创建字典树
  const root = new Tree();
  for (let product of products) {
    addWord(root, product);
  }

  // 对 searchWord 字符串进行遍历，添加到返回值二维数组中
  const ret: string[][] = [];
  let cur = root;
  for (let i = 0; i < searchWord.length; i++) {
    const ch = searchWord[i];
    // 如果找不到子节点，则创建一个空节点并赋值给 cur，进入下一轮循环
    cur = cur.children[ch] || new Tree();
    ret.push(cur.words);
  }

  return ret;
}

// @lc code=end

console.log(suggestedProducts(['mobile', 'mouse', 'moneypot', 'monitor', 'mousepad'], 'mouse'));
